INSERT INTO `wp_posts` VALUES ('22', '1', '2015-08-10 11:59:02', '2015-08-10 11:59:02', '<h3>[issuem_issue_title]</h3>', 'A sangre y letras', ' Escribir no es normal. Lo normal es leer y lo placentero es leer; incluso lo elegante es leer. Escribir es un ejercicio de masoquismo; leer a veces puede ser un ejercicio de sadismo, pero generalmente es una ocupación interesantísima. ', 'publish', 'open', 'open', '', 'inicio', '', '', '2016-01-18 13:48:56', '2016-01-18 13:48:56', '', '0', 'http://www.asangreyletras.es/revista/?page_id=22', '0', 'page', '', '0'); 
